class Person extends React.Component {
  state = {
    View: {
      name: 'Charlie',
      age: 27,
      address: 'Brooklyn, NY'
    },
    showView: false
  }

  toggleViewHandler = () => {
  const isVisible = this.state.showPerson;
  this.setState({
    showPerson: !isVisible
    });
  }
  
  render() {
    let person = null;
    if (this.state.showPerson) {
      person = (
      <div className="personCard">
        <p>Name: {this.state.person.name}</p>
        <p>Age: {this.state.person.age}</p>
        <p>Address: {this.state.person.address}</p>
      </div>
      );
    }
    return (
      <div className="container">
        <button 
          className="btn"
          onClick={this.togglePersonHandler}>
            Toggle
        </button>
        <div>{person}</div>
      </div>
    );
  }
}

ReactDOM.render(<Person />, document.querySelector('#app'))
